// components/ThemeCustomizer.js
import React, { useState } from "react";
import { getContrastingTextColor } from "../utils/colorUtils";

export default function ThemeCustomizer({ onThemeChange }) {
  const [primary, setPrimary] = useState("#007bff");
  const [secondary, setSecondary] = useState("#6c757d");
  const [background, setBackground] = useState("#ffffff");

  // Auto-generate contrast colors
  const textColor = getContrastingTextColor(background);
  const hoverColor = secondary;

  const handleChange = () => {
    onThemeChange({ primary, secondary, background, textColor, hoverColor });
  };

  return (
    <div>
      <h2>ðŸŽ¨ Customize Theme</h2>

      <label>Primary Color:</label>
      <input
        type="color"
        value={primary}
        onChange={(e) => setPrimary(e.target.value)}
      />
      <br />

      <label>Secondary Color:</label>
      <input
        type="color"
        value={secondary}
        onChange={(e) => setSecondary(e.target.value)}
      />
      <br />

      <label>Background Color:</label>
      <input
        type="color"
        value={background}
        onChange={(e) => setBackground(e.target.value)}
      />
      <br />

      <button onClick={handleChange} style={{ marginTop: "10px" }}>
        Apply Theme
      </button>
    </div>
  );
}
