// components/BookList.js
import React from "react";

export default function BookList({ books, selectedBook, onSelect }) {
  return (
    <div style={{ display: "flex", gap: "1rem", flexWrap: "wrap" }}>
      {books.map((book) => (
        <div
          key={book.id}
          onClick={() => onSelect(book)}
          style={{
            padding: "10px",
            border:
              selectedBook?.id === book.id
                ? "2px solid #000"
                : "1px solid #ccc",
            borderRadius: "8px",
            cursor: "pointer",
            backgroundColor: selectedBook?.id === book.id ? "#f0f0f0" : "white",
            transition: "0.3s",
          }}
        >
          <h3>{book.title}</h3>
          <p>{book.author}</p>
        </div>
      ))}
    </div>
  );
}
